import React, { Component } from "react";
import Router from "next/router";
import { BASE_URL } from "../config";
import axios from "axios";
import MainMenu from "../Components/Header/MainMenu";
import { Card, Image, Button, Header, Container } from "semantic-ui-react";

import moment from "moment";
const router = Router;
console.log(router);

function addComma(str) {
  let aa = str.toString().split("");
  if (aa.length === 7) {
    aa.splice(1, 0, ",");
  }
  if (aa.length === 6) {
    aa.splice(3, 0, ",");
  }
  if (aa.length === 5) {
    aa.splice(2, 0, ",");
  }
  if (aa.length === 4) {
    aa.splice(1, 0, ",");
  }
  if (aa.length === 3) {
    aa;
  }

  return aa.join("");
}

export default class confirmed extends Component {
  static async getInitialProps() {
    let confirms = [];
    try {
      const response = await axios.get(`${BASE_URL}/confirmed`);
      confirms = response.data;
      // console.table(confirms);
    } catch (error) {
      console.log(error);
    }
    return { confirms };
  }
  componentDidMount() {
    console.log("Yay");
  }

  // renderConfirmed(confirms) {
  //   return confirms.map((key) => {
  //     //  console.log(confirms);
  //     return confirms;
  //   });
  // }
  render() {
    const { confirms } = this.props;
    console.log(confirms);
    return (
      <>
        <MainMenu router={router} />
        <Container>
          <div className="main">
            <Card.Group className="card-main">
              hello
              {Object.keys(confirms).map((key, i) => (
                <Card className="center">
                  <Card.Content>
                    <Image
                      floated="right"
                      size="mini"
                      src=""
                      className="center"
                    />
                    <Card.Description className="lowerSpace">
                      Province state:
                      <strong>{confirms[key].provinceState}</strong>
                    </Card.Description>
                    <Card.Description className="lowerSpace">
                      Country Region:
                      <strong>{confirms[key].countryRegion}</strong>
                    </Card.Description>
                    <Card.Description className="lowerSpace">
                      Last Update:
                      <strong>
                        {moment(confirms[key].lastUpdate).format("YYYY-MM-DD")}
                      </strong>
                    </Card.Description>
                    <Card.Description className="lowerSpace">
                      Confirmed:
                      <strong>{addComma(confirms[key].confirmed)}</strong>
                    </Card.Description>
                    <Card.Description className="lowerSpace">
                      Active:
                      <strong>{addComma(confirms[key].active)}</strong>
                    </Card.Description>
                  </Card.Content>
                  <Card.Content extra>
                    <div className="ui two buttons">
                      <Button basic color="red">
                        Incident Rate: 8.34%
                      </Button>
                    </div>
                  </Card.Content>
                </Card>
              ))}
            </Card.Group>
          </div>
        </Container>
      </>
    );
  }
}
